mapper.update:
update Customer
set sessionKey   = #{sessionId},
    sessionLimit = #{next}
where userID = #{userID}

mapper.select:
select userID
from Customer
where userID = #{userID}

update customer
set userPwd = #{userPwd}
where userID = #{userID}

mapper.update:
update Customer
set userPwd=#{userPwd},
    userEmail=#{userEmail},
    phoneNo=#{phoneNo}
where userNo = #{userNo}

update Customer
set creditStatus=2
where userNo = #{userNo}

mapper.select:
select *
from (select rownum rn, A.*
      from (select * from Customer order by userNo desc) A)
where rn between #{start} and #{end}

SELECT COUNT(*)
FROM CUSTOMER
WHERE USERID = #{userID}
